var booleanAttrs = [
    'allowfullscreen'
   ,'async'
   ,'autofocus'
   ,'autoplay'
   ,'checked'
   ,'compact'
   ,'controls'
   ,'declare'
   ,'default'
   ,'defaultchecked'
   ,'defaultmuted'
   ,'defaultselected'
   ,'defer'
   ,'disabled'
   ,'draggable'
   ,'enabled'
   ,'formnovalidate'
   ,'hidden'
   ,'indeterminate'
   ,'inert'
   ,'ismap'
   ,'itemscope'
   ,'loop'
   ,'multiple'
   ,'muted'
   ,'nohref'
   ,'noresize'
   ,'noshade'
   ,'novalidate'
   ,'nowrap'
   ,'open'
   ,'pauseonexit'
   ,'readonly'
   ,'required'
   ,'reversed'
   ,'scoped'
   ,'seamless'
   ,'selected'
   ,'sortable'
   ,'spellcheck'
   ,'translate'
   ,'truespeed'
   ,'typemustmatch'
   ,'visible'
];

module.exports = {
    isBooleanAttr:
        function(name) {
            return booleanAttrs.indexOf(name.toLowerCase()) >= 0;
        }
};
